#!/usr/bin/env node

const exec = require('child_process').exec
const cmd  = process.argv.slice(2)

function startDetect() {
  const command = 'node ./node_modules/sherlockholmes/dist/server/app.js'
  const options = {
    maxBuffer: 1024 * 500
  }
  exec(`${command}`, options, (err) => {
    if (err) {
      console.log(`exec error: ${err}`)
      return
    }
    console.log('Starting detect...')
  })
}

if (!!cmd.length && cmd[0] === 'start') {
  startDetect()
}
